import React, { useState, useEffect } from "react";
import { Doctor } from "../types/doctor"; // ✅ use only this

interface Props {
  selectedDoctor: Doctor | null;
  onSave: (doctor: Doctor) => void;
}

const DoctorForm: React.FC<Props> = ({ selectedDoctor, onSave }) => {
  const [formData, setFormData] = useState<Doctor>({
    registrationNumber: "",
    doctorName: "",
    gender: "",
    phone: "",
    specialization: "",
    experience: 0,
    email: "",
    address: "",
  });

  useEffect(() => {
    if (selectedDoctor) {
      setFormData(selectedDoctor);
    }
  }, [selectedDoctor]);

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: name === "experience" ? +value : value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSave(formData);
    setFormData({
      registrationNumber: "",
      doctorName: "",
      gender: "",
      phone: "",
      specialization: "",
      experience: 0,
      email: "",
      address: "",
    });
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        maxWidth: "500px",
        margin: "auto",
        padding: "20px",
        border: "1px solid #ddd",
        borderRadius: "8px",
      }}
    >
      <h3>{selectedDoctor ? "Edit Doctor" : "Add Doctor"}</h3>
      <input
        type="text"
        name="registrationNumber"
        placeholder="Registration Number"
        value={formData.registrationNumber}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="doctorName"
        placeholder="Doctor Name"
        value={formData.doctorName}
        onChange={handleChange}
        required
      />
      <select
        name="gender"
        value={formData.gender}
        onChange={handleChange}
        required
      >
        <option value="">Select Gender</option>
        <option value="Male">Male</option>
        <option value="Female">Female</option>
      </select>
      <input
        type="text"
        name="phone"
        placeholder="Phone Number"
        value={formData.phone}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="specialization"
        placeholder="Specialization"
        value={formData.specialization}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="experience"
        placeholder="Experience (in years)"
        value={formData.experience}
        onChange={handleChange}
        required
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="address"
        placeholder="Address"
        value={formData.address}
        onChange={handleChange}
        required
      />
      <button type="submit" style={{ marginTop: "10px" }}>
        {selectedDoctor ? "Update Doctor" : "Add Doctor"}
      </button>
    </form>
  );
};

export default DoctorForm;
