import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

interface Doctor {
  selectedDoctor:  | null;
  registrationNumber: string;
  doctorName: string;
  gender: string;
  phone: string;
  email: string;
  department: string;
  specialization: string;
  services: string;
  picture: File | null;
  fee: string;
  timeSlot: string;
  receptionist: string;
  details: string;
}

const DoctorForm: React.FC = () => {
  const [formData, setFormData] = useState<Doctor>({
    registrationNumber: "D202511110004",
    doctorName: "",
    gender: "",
    phone: "",
    email: "",
    department: "",
    specialization: "",
    services: "",
    picture: null,
    fee: "",
    timeSlot: "",
    receptionist: "",
    details: "",
  });

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>
  ) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0] || null;
    setFormData((prev) => ({ ...prev, picture: file }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log("Submitted Data:", formData);
    alert("Doctor details submitted successfully!");
  };

  return (
    <div className="container mt-4 mb-5">
      <form
        onSubmit={handleSubmit}
        className="p-4 border rounded-3 shadow-sm bg-white"
      >
        <h4 className="text-center mb-4 fw-bold">Doctor Registration Form</h4>

        <div className="row g-3">
          {/* Registration Number */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Registration Number <span className="text-danger">*</span>
            </label>
            <input
              type="text"
              name="registrationNumber"
              className="form-control"
              value={formData.registrationNumber}
              readOnly
            />
          </div>

          {/* Doctor */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Doctor <span className="text-danger">*</span>
            </label>
            <select
              name="doctorName"
              className="form-select"
              value={formData.doctorName}
              onChange={handleChange}
              required
            >
              <option value="">Select Doctor</option>
              <option value="Dr. John">Dr. John</option>
              <option value="Dr. Smith">Dr. Smith</option>
            </select>
          </div>

          {/* Gender */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Gender <span className="text-danger">*</span>
            </label>
            <div>
              <div className="form-check form-check-inline">
                <input
                  type="radio"
                  name="gender"
                  value="Male"
                  checked={formData.gender === "Male"}
                  onChange={handleChange}
                  className="form-check-input"
                />
                <label className="form-check-label">Male</label>
              </div>
              <div className="form-check form-check-inline">
                <input
                  type="radio"
                  name="gender"
                  value="Female"
                  checked={formData.gender === "Female"}
                  onChange={handleChange}
                  className="form-check-input"
                />
                <label className="form-check-label">Female</label>
              </div>
              <div className="form-check form-check-inline">
                <input
                  type="radio"
                  name="gender"
                  value="Other"
                  checked={formData.gender === "Other"}
                  onChange={handleChange}
                  className="form-check-input"
                />
                <label className="form-check-label">Other</label>
              </div>
            </div>
          </div>

          {/* Phone Number */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Phone Number <span className="text-danger">*</span>
            </label>
            <input
              type="tel"
              name="phone"
              className="form-control"
              value={formData.phone}
              onChange={handleChange}
              required
            />
          </div>

          {/* Email */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Email <span className="text-danger">*</span>
            </label>
            <input
              type="email"
              name="email"
              className="form-control"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          {/* Department */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Departments <span className="text-danger">*</span>
            </label>
            <select
              name="department"
              className="form-select"
              value={formData.department}
              onChange={handleChange}
              required
            >
              <option value="">Select Department</option>
              <option value="Cardiology">Cardiology</option>
              <option value="Neurology">Neurology</option>
              <option value="Orthopedics">Orthopedics</option>
            </select>
          </div>

          {/* Specialization */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Specialization <span className="text-danger">*</span>
            </label>
            <input
              type="text"
              name="specialization"
              className="form-control"
              value={formData.specialization}
              onChange={handleChange}
              required
            />
          </div>

          {/* Services */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Services <span className="text-danger">*</span>
            </label>
            <input
              type="text"
              name="services"
              className="form-control"
              value={formData.services}
              onChange={handleChange}
              required
            />
          </div>

          {/* Picture */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Doctor Picture <span className="text-danger">*</span>
            </label>
            <input
              type="file"
              name="picture"
              className="form-control"
              accept="image/*"
              onChange={handleFileChange}
              required
            />
          </div>

          {/* Fee */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Consultation Fee <span className="text-danger">*</span>
            </label>
            <div className="input-group">
              <span className="input-group-text">â‚¹</span>
              <input
                type="number"
                name="fee"
                className="form-control"
                value={formData.fee}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          {/* Time Slot */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">
              Time Slot Duration <span className="text-danger">*</span>
            </label>
            <div className="input-group">
              <input
                type="number"
                name="timeSlot"
                className="form-control"
                placeholder="Minutes"
                value={formData.timeSlot}
                onChange={handleChange}
                required
              />
              <span className="input-group-text">Minute</span>
            </div>
          </div>

          {/* Receptionist */}
          <div className="col-md-3">
            <label className="form-label fw-semibold">Receptionist</label>
            <select
              name="receptionist"
              className="form-select"
              value={formData.receptionist}
              onChange={handleChange}
            >
              <option value="">Select Receptionist</option>
              <option value="R001">R001</option>
              <option value="R002">R002</option>
            </select>
          </div>

          {/* Details */}
          <div className="col-12">
            <label className="form-label fw-semibold">Details</label>
            <textarea
              name="details"
              className="form-control"
              rows={3}
              value={formData.details}
              onChange={handleChange}
            ></textarea>
          </div>
        </div>

        <div className="text-center mt-4">
          <button type="submit" className="btn btn-primary px-4">
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default DoctorForm;
