import React, { useState, useEffect } from "react";
import Swal from "sweetalert2";
import { Doctor } from "../types/doctor";

interface Props {
  selectedDoctor: Doctor | null;
  onSave: (doctor: Doctor) => void;
}

const DoctorForm: React.FC<Props> = ({ selectedDoctor, onSave }) => {
  const [formData, setFormData] = useState<Doctor>({
    doc_id: "",
    doctorName: "",
    department: "",
    gender: "",
    phone: "",
    specialization: "",
    experience: 0,
    email: "",
    address: "",
    services: "",
    fee: "",
    timeSlot: "",
    receptionist: "",
    details: "",
    picture: null,
  });

  useEffect(() => {
    if (selectedDoctor) {
      setFormData(selectedDoctor);
    } else {
      setFormData({
        doc_id: "",
        doctorName: "",
        department: "",
        gender: "",
        phone: "",
        specialization: "",
        experience: 0,
        email: "",
        address: "",
        services: "",
        fee: "",
        timeSlot: "",
        receptionist: "",
        details: "",
        picture: null,
      });
    }
  }, [selectedDoctor]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: name === "experience" ? +value : value });
  };

  const validateForm = (): boolean => {
    const requiredFields = [
      "doc_id",
      "doctorName",
      "department",
      "gender",
      "phone",
      "specialization",
      "experience",
      "email",
      "address",
      "services",
      "fee",
      "timeSlot",
      "receptionist",
      "details",
    ];

    for (let field of requiredFields) {
      if (!formData[field as keyof Doctor] || formData[field as keyof Doctor] === "") {
        Swal.fire({
          icon: "warning",
          title: "Missing Field",
          text: `Please fill out the "${field}" field.`,
        });
        return false;
      }
    }

    // Basic Email validation
    if (!/\S+@\S+\.\S+/.test(formData.email)) {
      Swal.fire({
        icon: "error",
        title: "Invalid Email",
        text: "Please enter a valid email address.",
      });
      return false;
    }

    // Phone validation
    if (!/^\d{10}$/.test(formData.phone)) {
      Swal.fire({
        icon: "error",
        title: "Invalid Phone Number",
        text: "Phone number must be 10 digits.",
      });
      return false;
    }

    return true;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    onSave(formData);

    Swal.fire({
      icon: selectedDoctor ? "info" : "success",
      title: selectedDoctor ? "Doctor Updated!" : "Doctor Added!",
      text: selectedDoctor
        ? `Doctor ${formData.doctorName} details updated successfully.`
        : `Doctor ${formData.doctorName} registered successfully!`,
      confirmButtonColor: "#3085d6",
    });

    setFormData({
      doc_id: "",
      doctorName: "",
      department: "",
      gender: "",
      phone: "",
      specialization: "",
      experience: 0,
      email: "",
      address: "",
      services: "",
      fee: "",
      timeSlot: "",
      receptionist: "",
      details: "",
      picture: null,
    });
  };

  return (
    <form onSubmit={handleSubmit} className="p-3 border rounded bg-light">
      <div className="row">
        <div className="col-md-4 mb-3">
          <label className="form-label">Doctor ID</label>
          <input
            type="text"
            name="doc_id"
            className="form-control"
            value={formData.doc_id}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Doctor Name</label>
          <input
            type="text"
            name="doctorName"
            className="form-control"
            value={formData.doctorName}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Department</label>
          <input
            type="text"
            name="department"
            className="form-control"
            value={formData.department}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Gender</label>
          <select
            name="gender"
            className="form-select"
            value={formData.gender}
            onChange={handleChange}
          >
            <option value="">Select</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
          </select>
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Phone</label>
          <input
            type="text"
            name="phone"
            className="form-control"
            value={formData.phone}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Specialization</label>
          <input
            type="text"
            name="specialization"
            className="form-control"
            value={formData.specialization}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Experience (years)</label>
          <input
            type="number"
            name="experience"
            className="form-control"
            value={formData.experience}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Email</label>
          <input
            type="email"
            name="email"
            className="form-control"
            value={formData.email}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-12 mb-3">
          <label className="form-label">Address</label>
          <textarea
            name="address"
            className="form-control"
            value={formData.address}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Services</label>
          <input
            type="text"
            name="services"
            className="form-control"
            value={formData.services}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Fee</label>
          <input
            type="text"
            name="fee"
            className="form-control"
            value={formData.fee}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Time Slot</label>
          <input
            type="text"
            name="timeSlot"
            className="form-control"
            value={formData.timeSlot}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-4 mb-3">
          <label className="form-label">Receptionist</label>
          <input
            type="text"
            name="receptionist"
            className="form-control"
            value={formData.receptionist}
            onChange={handleChange}
          />
        </div>

        <div className="col-md-12 mb-3">
          <label className="form-label">Details</label>
          <textarea
            name="details"
            className="form-control"
            value={formData.details}
            onChange={handleChange}
          />
        </div>
      </div>

      <button type="submit" className="btn btn-primary w-100">
        {selectedDoctor ? "Update Doctor" : "Add Doctor"}
      </button>
    </form>
  );
};

export default DoctorForm;
