import React, { useState, useEffect } from "react";
import { Student } from "../types/student";

interface Props {
  selectedStudent: Student | null;
  onSave: (student: Student) => void;
}

const StudentForm: React.FC<Props> = ({ selectedStudent, onSave }) => {
  const [formData, setFormData] = useState<Student>({
    name: "",
    age: 0,
    email: "",
    course: "",
  });

  useEffect(() => {
    if (selectedStudent) setFormData(selectedStudent);
  }, [selectedStudent]);

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: name === "age" ? +value : value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.name || !formData.email || !formData.course) {
      alert("Please fill all required fields");
      return;
    }
    onSave(formData);
    setFormData({ name: "", age: 0, email: "", course: "" });
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        width: "350px",
        margin: "20px auto",
        padding: "20px",
        border: "1px solid #ccc",
        borderRadius: "10px",
        background: "#f9f9f9",
      }}
    >
      <h3 style={{ textAlign: "center" }}>
        {selectedStudent ? "Edit Student" : "Add Student"}
      </h3>

      <label>Name:</label>
      <input
        type="text"
        name="name"
        value={formData.name}
        onChange={handleChange}
        required
        style={{ width: "100%", marginBottom: "10px" }}
      />

      <label>Age:</label>
      <input
        type="number"
        name="age"
        value={formData.age}
        onChange={handleChange}
        required
        style={{ width: "100%", marginBottom: "10px" }}
      />

      <label>Email:</label>
      <input
        type="email"
        name="email"
        value={formData.email}
        onChange={handleChange}
        required
        style={{ width: "100%", marginBottom: "10px" }}
      />

      <label>Course:</label>
      <input
        type="text"
        name="course"
        value={formData.course}
        onChange={handleChange}
        required
        style={{ width: "100%", marginBottom: "10px" }}
      />

      <button
        type="submit"
        style={{
          width: "100%",
          padding: "10px",
          background: "#1d4ed8",
          color: "#fff",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        {selectedStudent ? "Update" : "Submit"}
      </button>
    </form>
  );
};

export default StudentForm;
